import console.ConsoleOutput;import functionality.DataBase;import functionality.Shop;import java.io.IOException;import java.util.Scanner;public class Main {    public static void main(String[] args) throws IOException {        Main main = new Main();        main.run();    }    public void run(){        try {            Shop shop = new Shop();            DataBase db = new DataBase();            ConsoleOutput consoleOutput = new ConsoleOutput();            consoleOutput.showMainMenu();            Scanner scanner = new Scanner(System.in);            String st = scanner.next();            if(st.equals("1") || st.equals("shop") || st.equals("SHOP")){                shop.mainMenu();            }            else if (st.equals("2") || st.equals("Baza Danych") || st.equals("bd")){                db.mainMenu();            }            else {                System.out.println("Nieprawid≈Çowy parametr podaj 1 lub 2");            }        }        catch (Exception e){            System.out.println("smth went wrong...");            e.printStackTrace();        }    }}