package console;import inter.consoleOutputControl;import products.Product;import java.io.BufferedReader;import java.io.FileNotFoundException;import java.io.FileReader;import java.io.IOException;import java.util.ArrayList;public class ConsoleOutput implements consoleOutputControl {    public static final String db_path = "DataBase.txt";    /**Wyświela BD Header**/    public void showHeadDescription() {        System.out.println("*********************************************");        System.out.println("********System Sterowania Bazą Danych********");        System.out.println("*********************************************");        System.out.println("1)Wyswielić zawartość bazy danych");        System.out.println("2)Dodać product do bazy danych");        System.out.println("3)Usunąć product z bazy danych");        System.out.println("*********************************************");        System.out.println("Czekam na liczbe.... ");    }    /**Wyświala Menu Dodanie towaru**/    public void showAddDescription() {        System.out.println("**********************************************");        System.out.println("********Dodanie Produktu Do Bazy Danych*******");        System.out.println("**********************************************");        System.out.println("***************Co Muśimy Dodać****************");        System.out.println("***************WARZYW(1)/OWOC(2)**************");    }    /**Wyświala Menu Usunięcia towaru**/    public void showMainDeleteDescription (){        System.out.println("***********************************************");        System.out.println("********Usunięcie Produktu z Bazy Danych*******");        System.out.println("***********************************************");        System.out.println("*********Jak należy usunąc Prodct**************");        System.out.println("1) Usunąc tylko owoce");        System.out.println("2) Usunąc tylko warzywa");        System.out.println("3) Usunąc używając liczbę porządkową produktu**");        System.out.println("Czekam na liczbe...");    }    /**Wyświala Menu Usunięcia towaru używając liczbę porządkową**/    public void showIdDeleteDescription (){        System.out.println("***********************************************");        System.out.println("***Usunąc używając liczbę porządkową produktu**");        System.out.println("***********************************************");        System.out.println("Czekam na liczbe...");        System.out.println("Liczba może być zerem...");    }    /**Wyświela Shop Header**/    public void showShopMainMenu(){        System.out.println("***********************************************");        System.out.println("*********************SHOP**********************");        System.out.println("***********************************************");        System.out.println("1) Pokaż zawartośc Bazy Danych");        System.out.println("2) Kupic produkt");        System.out.println("Czekam na liczbe...");        System.out.println("Liczba może być zerem...");    }    /**Main Menu Header**/    public void showMainMenu(){        System.out.println("***********************************************");        System.out.println("***********************************************");        System.out.println("***********************************************");        System.out.println("1) Sklep");        System.out.println("2) Baza Danych");        System.out.println("Czekam na liczbe...");    }    /**Wyswietla zawartosc BD **/    public ArrayList<String> showDbContent() throws IOException {        ArrayList<String> list = new ArrayList<String>();        try {            String st;            BufferedReader br = new BufferedReader(new FileReader(db_path));            int value =0;            while ((st = br.readLine()) != null) {                System.out.println("Id: "+value +" | "+ st);                value++;                list.add(st);            }            br.close();        }        catch (FileNotFoundException fileNotFoundException){            System.out.println("Nie mogę znależć plik s Bazą Danych");        }        return list;    }    public String getDb_path() {        return db_path;    }}