package functionality;import products.Fruits;import products.Product;import products.Vegetable;import console.ConsoleOutput;import inter.consoleOutputControl;import java.io.*;import java.util.Scanner;public class DataBase {    /**Menu usunięcia Produktu **/    private void deleteProductMenu(){        ConsoleOutput consoleOutput =new ConsoleOutput();        consoleOutput.showMainDeleteDescription();        Scanner scanner = new Scanner(System.in);        try {            int value = scanner.nextInt();            switch (value){                case 1:{                    Fruits fruits = new Fruits();                    fruits.deleteProduct();                    break;                }                case 2:{                    Vegetable vegetable = new Vegetable();                    vegetable.deleteProduct();                    break;                }                case 3:{                    try {                        consoleOutput.showDbContent();                        System.out.println("Podaj Integer");                        System.out.println("Moży być 0");                        int indexToDolete = scanner.nextInt();                        Product vegetable = new Vegetable();                        vegetable.deleteProductById(indexToDolete);                        break;                    }                    catch (java.util.InputMismatchException inputMismatchException1 ){                        System.out.println("Podany parametr nie jest Integer, podaj inny");                        deleteProductMenu();                    }                }            }        }        catch (java.util.InputMismatchException inputMismatchException1){            System.out.println("Podany parametr nie jest Integer, podaj inny");        } catch (IOException e) {            e.printStackTrace();        }    }    /**Menu dodawania Produktu **/    private void addProductMenu() throws IOException {        try {            consoleOutputControl consoleOutput = new ConsoleOutput();            consoleOutput.showAddDescription();            Scanner scanner = new Scanner(System.in);            String type = scanner.next();            Product vegetable = new Vegetable();            Product fruits = new Fruits();            if (type.equals("WARZYW") || type.equals("1") || type.equals("W")) {                vegetable.appendProduct(vegetable);            } else if (type.equals("OWOC") || type.equals("2") || type.equals("O")) {                fruits.appendProduct(fruits);            } else {                System.out.println("Podany Parametr jest Błedny Prosze Podać 1 lub 2");                addProductMenu();            }        }        catch (java.util.InputMismatchException inputMismatchException){            System.out.println("Nieprawidłowy paramet");            addProductMenu();        }        catch (Exception e){            System.out.println("Smth went wrong...");            e.printStackTrace();        }    }    /**Menu sterowania Bazą Danych**/    public void mainMenu() throws IOException {        consoleOutputControl console = new ConsoleOutput();        console.showHeadDescription();        Scanner scanner = new Scanner(System.in);        try {            int respnose = scanner.nextInt();            switch (respnose) {                case 1:{                    console.showDbContent();                    break;                }                case 2:{                    addProductMenu();                    break;                }                case 3:{                    deleteProductMenu();                    break;                }            }        }        catch (java.util.InputMismatchException inputMismatchException){            System.out.println("PODANY PARAMETR NIE JEST INTEGER PODAJ INNY");            mainMenu();        }    }}