package products;import console.ConsoleOutput;import java.io.*;import java.util.ArrayList;import java.util.Scanner;public class Fruits extends Product{    private static final String type = "OWOC";    public Fruits(int cost, String name, int weight, String isGood) {        super(cost, name, weight, isGood);    }    public Fruits(){    }    /**Dodawanie Owoców **/    public void appendProduct(Product fruits) {        try {            ConsoleOutput consoleOutput = new ConsoleOutput();            PrintWriter out1 = new PrintWriter(new FileWriter(consoleOutput.getDb_path(), true));            Scanner scanner1 = new Scanner(System.in);            out1.print("TYP: " +type);            out1.print(" | ");            System.out.println("Podaj nazwe...");            String nazwa = scanner1.next();            fruits.setName(nazwa);            out1.print("NAZWA: " + fruits.getName());            out1.print(" | ");            System.out.println("Podaj cenę...");            try {                int cena = scanner1.nextInt();                fruits.setCost(cena);                out1.print("CENA: " + fruits.getCost());                out1.print(" | ");            } catch (java.util.InputMismatchException inputMismatchException) {                System.out.println("podany parametr nie jest INTEGER sprobuj ponownie");                appendProduct(fruits);            }            System.out.println("Podaj wagę...");            try {                int waga = scanner1.nextInt();                fruits.setWeight(waga);                out1.print("WAGA: " + fruits.getWeight());                out1.print(" | ");            } catch (java.util.InputMismatchException inputMismatchException1) {                System.out.println("podany parametr nie jest INTEGER sprobuj ponownie");                appendProduct(fruits);            }            System.out.println("Podaj stan...");            String stan = scanner1.next();            fruits.setGood(stan);            out1.print("STAN: " + fruits.getGood());            out1.print(" | ");            out1.println();            out1.close();            System.out.println(fruits.getName() + " " + fruits.getCost() + " " + fruits.getWeight() + " " + fruits.getGood());        }        catch (Exception error){            System.out.println("smth went wrong ....");            error.printStackTrace();        }    }    /**Menu usunięcia Produktu (należy podac numer porzadkowy elementa ktory należy usunąć)**/    public void deleteProduct() throws IOException {        ConsoleOutput consoleOutput = new ConsoleOutput();        consoleOutput.showDbContent();        consoleOutput.showMainDeleteDescription();        ArrayList<String> FruitList = new ArrayList<String>();        try {            try {                String st;                BufferedReader br = new BufferedReader(new FileReader(ConsoleOutput.db_path));                while ((st = br.readLine()) != null) {                    if (st.contains("TYP: OWOC |")) {                        FruitList.add(st);                    }                }                br.close();                for (int i = 0; i < FruitList.size(); i++) {                    FruitList.remove(i);                }                FileWriter f2 = new FileWriter(ConsoleOutput.db_path, false);                for (int i = 0; i < FruitList.size(); i++) {                    f2.write(FruitList.get(i) +"\n");                }                f2.close();            }            catch (FileNotFoundException fileNotFoundException){                System.out.println("NIE MOGE ZNALEZC PLIK Z BAZĄ DANYCH");            } catch (IOException e) {                e.printStackTrace();            }        }        catch (java.util.InputMismatchException inputMismatchException){            System.out.println("PODANY PARAMETR NIE JEST INTEGER PODAJ INNY");            deleteProduct();        }    }}