package products;import console.ConsoleOutput;import java.io.*;import java.util.ArrayList;import java.util.Scanner;public class Vegetable extends Product{    private static final String type = "WARZYW";    public Vegetable(int cost, String name, int weight, String isGood) {        super(cost, name, weight, isGood);    }    public Vegetable() {    }    /**Dodawanie Warzyw **/    public void appendProduct(Product vegetable) {        try {            PrintWriter out1 = new PrintWriter(new FileWriter(ConsoleOutput.db_path, true));            Scanner scanner1 = new Scanner(System.in);            out1.print("TYP: " + type);            out1.print(" | ");            System.out.println("Podaj nazwe...");            String nazwa = scanner1.next();            vegetable.setName(nazwa);            out1.print("NAZWA: " + vegetable.getName());            out1.print(" | ");            System.out.println("Podaj cenę...");            try {                int cena = scanner1.nextInt();                vegetable.setCost(cena);                out1.print("CENA: " + vegetable.getCost());                out1.print(" | ");            } catch (java.util.InputMismatchException inputMismatchException) {                System.out.println("podany parametr nie jest INTEGER sprobuj ponownie");                appendProduct(vegetable);            }            System.out.println("Podaj wagę...");            try {                int waga = scanner1.nextInt();                vegetable.setWeight(waga);                out1.print("WAGA: " + vegetable.getWeight());                out1.print(" | ");            } catch (java.util.InputMismatchException inputMismatchException1) {                System.out.println("podany parametr nie jest INTEGER sprobuj ponownie");                appendProduct(vegetable);            }            System.out.println("Podaj stan...");            String stan = scanner1.next();            vegetable.setGood(stan);            out1.print("STAN: " + vegetable.getGood());            out1.print(" | ");            out1.println();            out1.close();            System.out.println(vegetable.getName() + " " + vegetable.getCost() + " " + vegetable.getWeight() + " " + vegetable.getGood());        }        catch (Exception error){            System.out.println("smth went wrong ....");            error.printStackTrace();        }    }    /**Usunięcie Warzyw **/    public void deleteProduct() throws IOException {        ConsoleOutput consoleOutput = new ConsoleOutput();        consoleOutput.showDbContent();        consoleOutput.showIdDeleteDescription();        ArrayList<String> VegetableList = new ArrayList<String>();        try {            try {                String st;                BufferedReader br = new BufferedReader(new FileReader(ConsoleOutput.db_path));                while ((st = br.readLine()) != null) {                    if (st.contains("TYP: WARZYW")) {                        VegetableList.add(st);                    }                }                br.close();                for (int i = 0; i < VegetableList.size(); i++) {                    VegetableList.remove(i);                }                FileWriter f2 = new FileWriter(ConsoleOutput.db_path, false);                for (int i = 0; i < VegetableList.size(); i++) {                    f2.write(VegetableList.get(i) +"\n");                }                f2.close();            }            catch (FileNotFoundException fileNotFoundException){                System.out.println("NIE MOGE ZNALEZC PLIK Z BAZĄ DANYCH");            } catch (IOException e) {                e.printStackTrace();            }        }        catch (java.util.InputMismatchException inputMismatchException){            System.out.println("PODANY PARAMETR NIE JEST INTEGER PODAJ INNY");            deleteProduct();        }    }    /**Menu usunięcia Produktu (należy podac numer porzadkowy elementa ktory należy usunąć)**/    public void deleteProductById(int indexToDelete) throws IOException {        ConsoleOutput consoleOutput = new ConsoleOutput();        consoleOutput.showDbContent();        consoleOutput.showMainDeleteDescription();        ArrayList<String> list = new ArrayList<String>();        try {            try {                String st;                BufferedReader br = new BufferedReader(new FileReader(ConsoleOutput.db_path));                while ((st = br.readLine()) != null) {                    list.add(st);                }                br.close();                list.remove(indexToDelete);                FileWriter f2 = new FileWriter(ConsoleOutput.db_path, false);                for (int i = 0; i < list.size(); i++) {                    f2.write(list.get(i) +"\n");                }                f2.close();            }            catch (FileNotFoundException fileNotFoundException){                System.out.println("NIE MOGE ZNALEZC PLIK Z BAZĄ DANYCH");            } catch (IOException e) {                e.printStackTrace();            }        }        catch (java.util.InputMismatchException inputMismatchException){            System.out.println("PODANY PARAMETR NIE JEST INTEGER PODAJ INNY");            deleteProductById(indexToDelete);        }        finally {            System.out.println("Closed");        }    }}